function [softmaxModel, sampleOut, features] = trainCnnSoftmax(model, labeledImages, labels, options)	[trainImages, trainLabels, testImages, testLabels, trainSet, testSet] = sampleData4d(labeledImages, labels);		sampleOut = struct;	sampleOut.trainSet = trainSet;	sampleOut.testSet = testSet;	sampleOut.trainLabels = trainLabels;	sampleOut.testLabels = testLabels;	sampleOut.trainImages = trainImages;	sampleOut.testImages = testImages;		features = cnnComputeFeature(model, trainImages, options);	sofmaxOptions = struct;	sofmaxOptions.maxIter = options.softmaxIter;	sofmaxOptions.display = options.display;	numTrainImages = size(trainImages, 4);	inputSize = numel(features) / numTrainImages;	disp('training softmax...');	softmaxModel = softmaxTrain(inputSize,...		options.numClasses, options.softmaxLambda, features, trainLabels, sofmaxOptions);	disp('training softmax finished');end